mapper.insert:
INSERT INTO TBL_BUDGET_RECORDS(
	RECORD_ID
	, USER_ID
	, RECORD_AMOUNT
	, RECORD_TYPE
	, RECORD_CD
	, RECORD_DETAIL
	, RECORD_OUT_PAYMENT_CD
	, RECORD_DTM
	, RECORD_MEMO
	, RECORD_DEL_YN
	, UPD_DT
	, UPD_USER_ID
)
VALUES(
	  (select ifnull(max(a.record_id+0),0)+1 from TBL_BUDGET_RECORDS a)
    , #{userId}
    , #{recordAmount}
    , #{recordType}
    , #{recordCategory}
    , #{recordDetail}
    , #{recordPayment}
    , #{recordDtm}
    , #{recordMemo}
    , 'N'
    , sysdate()
    , #{userId} 
)

mapper.update:
UPDATE TBL_BUDGET_RECORDS
SET 


		RECORD_DEL_YN = 'Y'


		RECORD_AMOUNT    = #{recordAmount}
	, RECORD_TYPE     = #{recordType}
	, RECORD_CD		  = #{recordCategory}
	, RECORD_DETAIL   = #{recordDetail}
	, RECORD_OUT_PAYMENT_CD = #{recordPayment}
	, RECORD_DTM      = #{recordDtm}
	, RECORD_MEMO     = #{recordMemo}
	, UPD_DT          = sysdate() 
	, UPD_USER_ID     = #{userId} 


WHERE 1=1
AND RECORD_ID = #{recordId}
AND USER_ID = #{userId}
