mapper.select:
SELECT USER_ID
FROM TBL_USER_INFO
WHERE USER_EMAIL = #{user_email}

SELECT *
FROM TBL_USER_INFO
WHERE USER_ID = #{user_id}
AND USER_PW = #{user_pw}

SELECT *
FROM TBL_USER_INFO
WHERE USER_ID = #{user_id}
AND USER_EMAIL = #{user_email}

SELECT USER_ID
FROM TBL_USER_INFO
WHERE USER_ID = #{check_id}

SELECT USER_EMAIL
FROM TBL_USER_INFO
WHERE USER_EMAIL = #{check_email}

mapper.update:
UPDATE TBL_USER_INFO
SET USER_PW = #{user_pw}
WHERE USER_ID = #{user_id}

mapper.select:
SELECT USER_PW
FROM TBL_USER_INFO
WHERE USER_ID = #{user_id}
